/**
 718. Maximum Length of Repeated Subarray
 Medium

 4414

 109

 Add to List

 Share
 Given two integer arrays nums1 and nums2, return the maximum length of a subarray that appears in both arrays.

  

 Example 1:

 Input: nums1 = [1,2,3,2,1], nums2 = [3,2,1,4,7]
 Output: 3
 Explanation: The repeated subarray with maximum length is [3,2,1].
 Example 2:

 Input: nums1 = [0,0,0,0,0], nums2 = [0,0,0,0,0]
 Output: 5
  

 Constraints:

 1 <= nums1.length, nums2.length <= 1000
 0 <= nums1[i], nums2[i] <= 100
 Accepted
 193,520
 Submissions
 376,706
 */



class Solution {
    func findLength(_ nums1: [Int], _ nums2: [Int]) -> Int {
        if nums1 == nums2 {
            return nums1.count
        }
        
        var hashMap: Dictionary<String, [Int]> = [:]
        var maxCount = 0
        for (i,v) in nums2.enumerated() {
            if let values = hashMap[String(v)] {
                hashMap[String(v)] = values + [i]
            } else {
                hashMap[String(v)] = [i]
            }
        }
        
        for (i,v) in nums1.enumerated() {
            if let values = hashMap[String(v)] {
                for j in values {
                    var k = 0
                    while i + k < nums1.count && j + k < nums2.count && nums1[i + k] == nums2[j + k] {
                        k += 1
                    }
                    maxCount = max(maxCount, k)
                }
            }
        }
        
        return maxCount

    }
}


let sol = Solution()
print(sol.findLength([1,2,3,2,1], [3,2,1,4,7]))
